mapper.update:
update art46.BRIEF
set
    commentaar = #{commentaar}
where brief_id = #{brief_id}

update art46.BRIEF
set
    check_afd_hfd_d = current date,
    opmerking_afd_hfd = #{opmerking_afd_hfd}
where brief_id = #{brief_id}

mapper.select:
select count(*)
from art46.brief br
where 1 = 1
    and br.qr_code = #{qr_code}

        and br.BRIEF_ID != #{brief_id}

SELECT brief.brief_id as value, CONCAT(CONCAT(CONCAT(brief.BRIEF_NR,': '), CONCAT(COALESCE(brief.betreft, ''), ' ')),COALESCE(brief.COMMENTAAR, '')) as label FROM ART46.BRIEF brief where brief.CATEGORIE_ID = 5 and brief.BESTEK_ID = #{bestekId} and brief.brief_id not in (Select GUNNINGSBESLISSING from ART46.AANVRAAGVASTLEGGING where id != #{aanvraagId} and GUNNINGSBESLISSING is not null) and brief.brief_id not in (Select GUNNINGSVERSLAG from ART46.AANVRAAGVASTLEGGING where id != #{aanvraagId} and GUNNINGSVERSLAG is not null) and brief.brief_id not in (Select briefid from ART46.AANVRAAGVASTLEGGINGBRIEVEN where aanvraagid != #{aanvraagId} and briefid is not null) and brief.DMS_ID is NOT NULL
and brief.UIT_TYPE_ID_VOS in
#{item}



            inner join ART46.BESTEK be
            on br.dossier_id = be.dossier_id
            and upper(be.bestek_nr) like '%' || UPPER(#{bestek_nr}) || '%'

where 1 = 1

        and upper(br.adres_naam) like '%' || UPPER(#{adres_naam}) || '%'



        and br.inschrijf_d >= #{inschrijf_d_van}


        and br.inschrijf_d <= #{inschrijf_d_tot}



        and br.in_d >= #{in_d_van}


        and br.in_d <= #{in_d_tot}



        and br.uit_d >= #{uit_d_van}


        and br.uit_d <= #{uit_d_tot}



        and upper(br.betreft) like '%' || UPPER(#{betreft}) || '%'


        and upper(br.brief_nr) like '%' || UPPER(#{brief_nr}) || '%'


        and br.auteur_id = #{auteur_id}


        and upper(br.in_referte) like '%' || UPPER(#{in_referte}) || '%'


        and upper(br.dossier_nr) like '%' || UPPER(#{dossier_nr}) || '%'


        and br.doss_hdr_id = #{dossier_hdr_ivs}


        and upper(br.dossier_b) like '%' || UPPER(#{dossier_b_ivs}) || '%'


        and br.dossier_fase_id = #{dossier_fase_id}


        and br.dossier_id_boa = #{dossier_id_boa}


        and upper(br.gemeente_b) like '%' || UPPER(#{dossier_gemeente}) || '%'


        and br.qr_code = #{qr_code}


        and br.in_aard_id = #{in_aard_id}


        and br.uit_aard_id = #{uit_aard_id}

order by br.brief_id desc
fetch first 5001 rows only

select coalesce(max(volgnummer), 0)
from art46.brief
where dossier_id = #{dossier_id}
