mapper.select:
with overdrachten_historiek as ( select doh.* from art46.v_dossier_overdracht_historiek doh where 1 = 1
and doh.overdracht_id = #{overdracht_id}
and doh.dossier_id_boa = #{dossier_id_boa}
and doh.dossier_b = #{dossier_b}
) select doso.overdracht_id, doso.versie_nr, doso.dossier_id, doso.dossier_type, dos.gemeente_b, coalesce(dos.dossier_b, doso.dossier_b, sd.naam) as dossier_omschrijving, doso.dossier_id_boa, dos.dossier_nr, df.fase_b, rg.rechtsgrond_b, dt.doelgroep_type_b, doso.timing_jaar, doso.timing_maand, doso.prioriteits_index, doso.commentaar, be.bestek_nr as screen_bestek_omschrijving, doso.status_start_d, doso_status.omschrijving as status_b, doso.deleted_jn, dos.doss_hdr_id from overdrachten_historiek doso inner join art46.dossier_overdracht_status doso_status on doso.status = doso_status.code left join smeg.dossier sd on doso.DOSSIER_ID_BOA = sd.dossier_id left join art46.v_dossier dos on doso.DOSSIER_ID = dos.id left join art46.dossier_rechtsgrond rg on dos.dossier_type = rg.dossier_type and dos.rechtsgrond_code = rg.rechtsgrond_code left join art46.dossier_fase df on dos.dossier_type = df.dossier_type and dos.dossier_fase_id = df.fase_id left join art46.doelgroep_type dt on dos.doelgroep_type_id = dt.doelgroep_type_id left join art46.bestek be on doso.screen_bestek_id = be.bestek_id order by doso.overdracht_id, doso.versie_nr

select sd.id as smeg_id, sd.naam as smeg_naam,
dos.id as ivs_id, dos.dossier_nr as ivs_dossier_nr, dos.dossier_b as ivs_dossier_b, dos.rechtsgrond_code as ivs_rechtsgrond_code, dos.nis_id as ivs_nis_id, dos.doss_hdr_id as ivs_doss_hdr_id, dos.dossier_fase_id as ivs_dossier_fase_id, dos.programma_code as ivs_programma_code,
doso.overdracht_id from smeg.dossier sd left join art46.v_dossier dos on sd.dossier_id = dos.dossier_id_boa left join art46.DOSSIER_OVERDRACHT doso on dos.id = doso.dossier_id and doso.deleted_jn = 'N' where sd.dossier_id = #{dossier_id_boa}
